        public static String ToHexString(Byte[] Buffer) {
            StringBuilder Builder = new StringBuilder((Buffer.Length * 2) - 1);
            for (Int32 Index = 0; Index <= Buffer.Length - 1; Index++)
                Builder.Append(ToHexDict[Buffer[Index]]);
            String Result = Builder.ToString();

            Array.Clear(Buffer, 0, Buffer.Length);
            Array.Resize(ref Buffer, 0);

            Builder.Clear();

            return Result;
        }

        public static Byte[] FromHexString(String Input) {
            if (Input.Length < 2)
                return [];
            Char[] LookupValue = new Char[2];
            Byte[] Buffer = new Byte[(Input.Length / 2) - 1 + 1];
            for (Int32 Index = 0; Index <= Input.Length - 1; Index += 2) {
                LookupValue[0] = Input[Index + 0];
                LookupValue[1] = Input[Index + 1];
                Buffer[Index / 2] = FromHexDict[new String(LookupValue)];
            }
            
            return Buffer;
        }
        
        public static String ToHex(Byte Value) {
            return ToHexDict[Value];
        }

        public static Byte FromHex(String Input, Int32 Offset) {
            Char[] LookupValue = new Char[2];
            LookupValue[0] = Input[Offset + 0];
            LookupValue[1] = Input[Offset + 1];
            return FromHexDict[new String(LookupValue)];
        }
